import React, { Component } from "react";
import data from "../../data/videos.json";
import AsideCard from "../AsideCard/AsideCard";

function nextVideos(video) {
  return (
    <>
      <img className="aside__image" src={video.image} alt={video.title} />
      <p>{video.title}</p>
      <p>{video.channel}</p>
    </>
  );
}

